# Função para montar o prompt
def montar_prompt_curso(nome_curso, grupo):
    prompt = f"Curso: {nome_curso}\n"
    ucs = grupo.groupby(['id_UC', 'Título UC'])
    
    for (id_uc, titulo_uc), subgrupo in ucs:
        if titulo_uc.startswith("Projeto Integrador") or titulo_uc.startswith("Estágio Profissional") or titulo_uc.startswith("Prática Profissional") or titulo_uc.startswith("Prática Integrada"):
            continue
        
        prompt += f"\nTítulo da UC: {titulo_uc} ({id_uc})\n"
        
        for _, row in subgrupo.iterrows():
            if row['Tipo'] == 'Indicadores':
                prompt += f"Indicador: {row['Descrição']} ({row['id_D']})\n"
            elif row['Tipo'] == 'Conhecimentos':
                prompt += f"Conhecimento: {row['Descrição']} ({row['id_D']})\n"
            elif row['Tipo'] == 'Habilidades':
                prompt += f"Habilidade: {row['Descrição']} ({row['id_D']})\n"
    
    return prompt

# Função para processar resposta
def process_response_data(response_lines):
    headers = "id_UC;cat_tec;tec;justificativa1;substituição;geração;ampliação;transferência;justificativa2;horizonte;justificativa3;relação;justificativa4"
    lines = response_lines.split("\n")
    start_index = 0

    # Encontrar o índice da linha que contém os headers
    for i, line in enumerate(lines):
        if line.strip() == headers:
            start_index = i
            break

    processed_data = []
    current_line_data = []

    # Processar as linhas começando após os headers
    for line in lines[start_index + 1:]:
        line_data = line.split(";")
        if len(line_data) == 13:
            processed_data.append(line_data)
        else:
            current_line_data.extend(line_data)
            if len(current_line_data) == 13:
                processed_data.append(current_line_data)
                current_line_data = []

    return headers.split(";"), processed_data

# Iterar sobre os cursos
